function getTaxonomyObj() {
	var dataElement = document.getElementById('js_taxonomy_data_f01f9219-f179-41d0-94c9-4687a76ecc3a');
	var taxonomyObj = JSON.parse(dataElement.textContent);
	if (taxonomyObj) {
