		return transformTaxonomyForKrux(taxonomyObjForPage);
	}
	return {};
}

// Because rnwy doesn't support all json data structures, we have to do some manual stitching here.
function transformTaxonomyForKrux(taxonomy) {
	if (taxonomy && taxonomy.productInfo) {
		taxonomy.productInfo = taxonomy.productInfo.map(function (pInfo) {
			pInfo.price = parseFloat(pInfo.price);
			if (pInfo.averageReviewRating) {
				pInfo.averageReviewRating = parseFloat(pInfo.averageReviewRating);
			}
			pInfo.categoryTreeList = pInfo.categoryTreeList.map(function (categoryTree) { return categoryTree.tree });
			return pInfo;
		});
	}
	return taxonomy;
}


